import React, { useEffect, useState } from "react";
import {useLocation} from 'react-router-dom';
import Search from "./Search"
import "./tbl.css"

import Axios from "axios"
import { useNavigate } from "react-router-dom";

export default function App() {
    const navigate = useNavigate();
    let [datacd, setDatacd] = useState([]);
    let [str, setStr] = useState('');
    const { state } = useLocation();
 
    console.log("in MyApplication element");
    console.log(state);
    useEffect(()=>{
        const fetchall = async () =>{
            
            var url=""
            var colortxt="#686868"

            if(state=="i-box1a")
            {
                setStr("Shortlisted")
                url="/shortlisted"
                colortxt="#FFB800"
            }
            if(state=="i-box2a")
            {
                setStr("Interviewing")
                url="/interview"
                colortxt="#FFB800"
            }if(state=="i-box3a")
            {
                setStr("Rejected")
                url="/reject"
                colortxt="#FFB800"

            }if(state=="i-box4a")
            {
                setStr("Offer Recieved")
                url="/offer"
            }
            try{
                let res = await Axios.get(url); 
         
            console.log(res.data);
            console.log("Inside Myapplication Menu");
            console.log(str);
            setDatacd(res.data)

        }catch(e)
        {
            
            console.log(e);
            navigate(-1)
        }

        }
        fetchall()
    },[])
    console.log("data");
    console.log(datacd.length);
    console.log(str);

    return (
        <>
           <Search str={str}></Search>
           <table>
                <th>Job Code</th>
                <th>Job Title</th>
                <th>Location</th>
                <th>Posted Date</th>
                <th>Applied Date</th>
                <th>Status</th>
           {datacd.length?datacd.map((a)=>
            <tr>
                <td style={{color:"#006FCB"}}>{a.jobcode}</td>
                <td>{a.jobtitle}</td>
                <td>{a.location}</td>
                <td>{a.posteddate}</td>
                <td>{a.applieddate}</td>
                <td>{a.candidateappstatus}</td>
            </tr>
            

           ):<div>No data</div>}
           </table>
        </>

    )

}
