import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import './card.css';
import { useLocation } from 'react-router-dom';


export default function Card({ jobdata, app }) {
    console.log(jobdata);
    console.log(app);

    useEffect(() => {
        let str = `applyBtn gen${jobdata.id}`
        let appbutt = document.querySelectorAll(".applyBtn")
        console.log("------------");
        console.log(appbutt);
        for (let i = 0; i < appbutt.length; ++i) {
            appbutt[i].addEventListener("click", (e) => {
                // console.log(e.target.className)
                // console.log(str)
                // console.log(e.target.className == str);
                const fetchall = async () => {
                    try {
                        var today = new Date(),
 
    newDate = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
                        console.log(newDate);
                        let data = { "jobid": jobdata.id, "jobcode": jobdata.jobcode,"jobtitle": jobdata.jobtitle,"location": jobdata.location,"candidateappstatus":"Applied","posteddate": jobdata.posteddate,"applieddate": newDate}

                        // let res = await Axios.post("/apply",data);
                        console.log("------------");
                        console.log(data);





                    } catch (e) {

                        console.log(e);
                    }

                }
                if (e.target.className == str) {
                    console.log("call fetch");
                    fetchall()
                }

            })
        }




    }, [])
    let butclass = `applyBtn gen${jobdata.id}`
    console.log(butclass);
    return (

        <div className='cont-card'>
            <div className='card'>
                <div className='headerText'>
                    <text className='Titletext'>{jobdata.jobCode} {jobdata.jobtitle}</text>

                    {app ? <button className={butclass}>Apply</button> : <div className='applied'><i className="fa fa-check"></i>Applied</div>}
                </div>
                <div className='line2'>
                    <div className='jobLocation'><img src='/images/location_on.png'></img><text>{jobdata.location}</text></div>
                    <div className='jobType'><img src='/images/briefcase-outline.png'></img><text>{jobdata.jobtype}</text></div>
                    <div className='jobSalary'><img src='/images/money.png'></img><text>{jobdata.salary}</text></div>
                    <div className='jobCategory'><img src='/images/building.png'></img><text>{jobdata.companyname.toUpperCase()}</text></div>
                </div>
                <div className='jobDescription'>
                    <p>
                        {jobdata.jobdescription}
                    </p>
                </div>
            </div>
        </div>


    );
}
