import React, { useEffect, useState } from "react";

import "./subsrch.css"
export default function App() {
    const [value, setValue] = useState('');
    const [srchval, setSrchval] = useState({});

    const handleChange = (e) => {
        setValue(e.target.value);
        console.log(value);
        
        setSrchval({
            
            [e.target.name]: e.target.value
        })
        console.log(srchval);

    };
    return (
        <>
            <div className="sub-main">
                <div className="subcontainer">
                    <div className="subin" >


                        <select className="ardsub"name="domain" value={value} onChange={handleChange}>

                            <option value="all">All Jobs</option>

                            <option value="java">JAVA</option>

                            <option value="react">React</option>

                        </select>


                    </div>
                    <div className="subin"><div className="ardsub">Industry</div>
                    </div>
                    <div className="subin"><div className="ardsub">Location</div>
                    </div>
                    <div className="subin" ><div className="ardsub">Job type</div>
                    </div>
                    <div className="subin"><div className="ardsub">Posted date</div>
                    </div>
                </div>
            </div>
        </>

    )

}
